import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";

function BookCard(props) {
  const [selectedBooks, setSelectedBooks] = useState([]);

  function toggleSelected() {
    let toggle = !selectedBooks;
    setSelectedBooks(toggle);
    console.log(toggle);
  }
  return (
    <Card key={props.book.asin} className="col-3">
      <Card.Img variant="top" src={props.book.img} className="object-fit-cover" onClick={toggleSelected()} />
      <Card.Body>
        <Card.Title>{props.book.title}</Card.Title>
        <Card.Text>
          <p>DART KEBAB APPROVED!</p>
          <p>{props.book.category}</p>
          <p>{props.book.price}</p>
        </Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
  );
}

export default BookCard;
