import React from "react";
import { Row, Col } from "react-bootstrap";
import BookCard from "./BookCard"; // Assicurati che il percorso sia corretto
import fantasy from "../books/fantasy.json";
// Funzione che restituisce una lista di BookCard all'interno di una griglia
function FantasyBookList({ fantasy }) {
  return (
    <Row className="gy-3">
      {fantasy.map((fantasybook, index) => (
        <Col sm={3} lg={2} key={`cardBook-${index}`}>
          <BookCard book={fantasybook} />
        </Col>
      ))}
    </Row>
  );
}

export default FantasyBookList;
